/// <reference types="react" />
/**
 * Creates the react component that will be displayed in the pills.
 */
export interface IPillContentProvider<T extends IPillContentConfig> {
    /**
     * Custom properties unique to this pill content that are passed in to the props parameter when Pill calls createContent.
     */
    contentProps: T;
    /**
     * Function called by Pill to create the inner pill content.
     */
    createContent(editMode: boolean, props: T, updateConfiguredStatus: (configured: boolean) => void, updateAriaLabel: (newLabel: string) => void, editingComplete?: () => void, removeSelf?: () => void): Promise<JSX.Element>;
}
export interface IPillContentConfig {
    /**
     * When true editing the pill is disabled. Otherwise pill editing is enabled.
     */
    disableEdit?: boolean;
    /**
     * Custom aria label to prepend to this pill's built-in label.
     */
    ariaLabel?: string;
    /**
     * Enables Checkbox when pill is completely configured and in editMode.
     */
    showCheckboxComplete?: boolean;
    pillName?: string;
}
