import { IPillContentConfig } from "./IPillContent";
export interface TextPillConfigData extends IPillContentConfig {
    /**
     * Text to display in the pill
     */
    text: string;
}
export interface TextPillConfig extends TextPillConfigData {
    /**
     * Callback that notifies control consumer that the pill text value has been changed by the user.
     * Pill expects the pill control consumer to update the text property with the new text.
     */
    onTextUpdated(newText: string): void;
}
export interface TextPillProps extends TextPillConfig {
    editMode: boolean;
    updateConfiguredStatus: (configured: boolean) => void;
    updateAriaLabel: (newLabel: string) => void;
    editingComplete: () => void;
}
